import './App.css';
import ClienteForm from './components/ClienteForm';
import ClienteList from './components/ClienteList';
import ProductoForm from './components/ProductoForm';
import ProductoList from './components/ProductoList';
import { useState, useEffect } from 'react';
import { getClientes, addCliente, getProductos, addProducto } from './services/api';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import StorefrontIcon from '@mui/icons-material/Storefront';

function App() {
  const [clientes, setClientes] = useState([]);
  const [productos, setProductos] = useState([]);

  useEffect(() => {
    getClientes().then(setClientes);
    getProductos().then(setProductos);
  }, []);

  const handleAddCliente = async (cliente) => {
    await addCliente(cliente);
    const nuevos = await getClientes();
    setClientes(nuevos);
  };

  const handleAddProducto = async (producto) => {
    await addProducto(producto);
    const nuevos = await getProductos();
    setProductos(nuevos);
  };

  return (
    <Box sx={{ flexGrow: 1, bgcolor: '#f4f6f8', minHeight: '100vh' }}>
      <AppBar position="static" sx={{ bgcolor: '#3949ab' }}>
        <Toolbar>
          <StorefrontIcon sx={{ mr: 2, fontSize: 32 }} />
          <Typography variant="h6" component="div" sx={{ flexGrow: 1, letterSpacing: 2 }}>
            Sistema de Ventas
          </Typography>
        </Toolbar>
      </AppBar>
      <Container maxWidth="lg" sx={{ mt: 4 }}>
        <Box sx={{ display: 'flex', gap: 4, flexWrap: 'wrap', justifyContent: 'center' }}>
          <Box sx={{ minWidth: 320, maxWidth: 400, flex: 1 }}>
            <ClienteForm onSubmit={handleAddCliente} />
            <ClienteList clientes={clientes} />
          </Box>
          <Box sx={{ minWidth: 320, maxWidth: 400, flex: 1 }}>
            <ProductoForm onSubmit={handleAddProducto} />
            <ProductoList productos={productos} />
          </Box>
        </Box>
      </Container>
    </Box>
  );
}

export default App;
