import React, { useState } from 'react';
import ChatIcon from '@mui/icons-material/Chat';
import Dialog from '@mui/material/Dialog';
import { Box, Typography, Paper, Grid, Button, Avatar, Divider, List, ListItem, ListItemIcon, ListItemText, Stack } from '@mui/material';
import DashboardIcon from '@mui/icons-material/Dashboard';
import PeopleIcon from '@mui/icons-material/People';
import Inventory2Icon from '@mui/icons-material/Inventory2';
import BarChartIcon from '@mui/icons-material/BarChart';
import LogoutIcon from '@mui/icons-material/Logout';
import AddIcon from '@mui/icons-material/Add';
import TableChartIcon from '@mui/icons-material/TableChart';

const metrics = [
  { label: 'Ventas Hoy', value: '$2,350', icon: <BarChartIcon sx={{ color: '#00bcd4', fontSize: 36 }} />, color: '#e3f2fd' },
  { label: 'Clientes', value: '128', icon: <PeopleIcon sx={{ color: '#3949ab', fontSize: 36 }} />, color: '#fffde7' },
  { label: 'Productos', value: '56', icon: <Inventory2Icon sx={{ color: '#ff9800', fontSize: 36 }} />, color: '#fbe9e7' },
];

const tableData = [
  { id: 1, cliente: 'María López', producto: 'Laptop', total: '$1,200', fecha: '22/08/2025' },
  { id: 2, cliente: 'Carlos Pérez', producto: 'Mouse', total: '$25', fecha: '22/08/2025' },
  { id: 3, cliente: 'Ana Torres', producto: 'Monitor', total: '$180', fecha: '21/08/2025' },
  { id: 4, cliente: 'Luis Gómez', producto: 'Teclado', total: '$45', fecha: '21/08/2025' },
];

const UserDashboard = () => {
  const [openChat, setOpenChat] = useState(false);
  const [chatInput, setChatInput] = useState("");
  const [chatMessages, setChatMessages] = useState([
    { from: 'bot', text: '¡Hola! ¿En qué puedo ayudarte hoy?' }
  ]);

  const handleSend = (e) => {
    e.preventDefault();
    if (!chatInput.trim()) return;
    setChatMessages([...chatMessages, { from: 'user', text: chatInput }]);
    setTimeout(() => {
      setChatMessages(msgs => ([...msgs, { from: 'bot', text: 'Gracias por tu mensaje. Pronto te responderemos.' }]));
    }, 800);
    setChatInput("");
  };

  return (
    <Box sx={{
      display: 'flex',
      minHeight: '100vh',
      bgcolor: 'linear-gradient(135deg, #e3f0ff 0%, #f8fafc 100%)',
      position: 'relative',
      width: '100vw',
      overflowX: 'hidden',
    }}>
      {/* Sidebar */}
      <Box sx={{
        width: { xs: 70, md: 220 },
        minWidth: { xs: 70, md: 220 },
        maxWidth: { xs: 70, md: 220 },
        bgcolor: 'linear-gradient(120deg, #23272f 60%, #3949ab 100%)',
        color: '#fff',
        display: 'flex',
        flexDirection: 'column',
        alignItems: { xs: 'center', md: 'flex-start' },
        py: 4,
        px: { xs: 1, md: 2 },
        boxShadow: 6,
        zIndex: 2,
        height: '100vh',
        position: 'fixed',
        left: 0,
        top: 0,
      }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 4, width: '100%' }}>
          <Avatar sx={{ bgcolor: '#00bcd4', width: 48, height: 48, mr: { md: 2 } }}>U</Avatar>
          <Typography variant="h6" fontWeight={700} sx={{ display: { xs: 'none', md: 'block' } }}>Usuario</Typography>
        </Box>
        <List sx={{ width: '100%' }}>
          <ListItem button selected>
            <ListItemIcon sx={{ color: '#fff' }}><DashboardIcon /></ListItemIcon>
            <ListItemText primary="Dashboard" sx={{ display: { xs: 'none', md: 'block' } }} />
          </ListItem>
          <ListItem button>
            <ListItemIcon sx={{ color: '#fff' }}><PeopleIcon /></ListItemIcon>
            <ListItemText primary="Clientes" sx={{ display: { xs: 'none', md: 'block' } }} />
          </ListItem>
          <ListItem button>
            <ListItemIcon sx={{ color: '#fff' }}><Inventory2Icon /></ListItemIcon>
            <ListItemText primary="Productos" sx={{ display: { xs: 'none', md: 'block' } }} />
          </ListItem>
          <ListItem button>
            <ListItemIcon sx={{ color: '#fff' }}><TableChartIcon /></ListItemIcon>
            <ListItemText primary="Ventas" sx={{ display: { xs: 'none', md: 'block' } }} />
          </ListItem>
        </List>
        <Box sx={{ flexGrow: 1 }} />
        <Button startIcon={<LogoutIcon />} sx={{ color: '#fff', mt: 2, display: { xs: 'none', md: 'flex' } }} fullWidth>
          Cerrar sesión
        </Button>
      </Box>
      {/* Main Content */}
      <Box sx={{
        flex: 1,
        ml: { xs: '70px', md: '220px' },
        p: { xs: 2, md: 5 },
        display: 'flex',
        flexDirection: 'column',
        gap: 4,
        minWidth: 0,
        width: '100%',
        maxWidth: '100vw',
        boxSizing: 'border-box',
      }}>
        {/* Header */}
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 2 }}>
          <Typography variant="h4" fontWeight={900} color="#23272f">Panel de Control</Typography>
          <Button variant="contained" startIcon={<AddIcon />} sx={{ bgcolor: '#00bcd4', fontWeight: 700, px: 3, ':hover': { bgcolor: '#0097a7' } }}>
            Nueva Venta
          </Button>
        </Box>
        {/* Metrics Cards */}
        <Grid container spacing={3}>
          {metrics.map((m, i) => (
            <Grid item xs={12} md={4} key={i}>
              <Paper elevation={6} sx={{ p: 3, display: 'flex', alignItems: 'center', gap: 2, bgcolor: m.color, borderRadius: 4, boxShadow: '0 8px 32px #3949ab22' }}>
                {m.icon}
                <Box>
                  <Typography variant="body2" color="text.secondary">{m.label}</Typography>
                  <Typography variant="h5" fontWeight={700}>{m.value}</Typography>
                </Box>
              </Paper>
            </Grid>
          ))}
        </Grid>
        {/* Chart Placeholder */}
        <Paper elevation={6} sx={{ p: 4, borderRadius: 4, minHeight: 260, mb: 2, boxShadow: '0 8px 32px #00bcd422', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
          <Typography variant="h6" fontWeight={700} color="#3949ab" mb={2}>Gráfica de Ventas (próximamente)</Typography>
          <Box sx={{ width: '100%', height: 160, bgcolor: '#e3f0ff', borderRadius: 2, display: 'flex', alignItems: 'center', justifyContent: 'center', color: '#b0bec5', fontSize: 32, fontWeight: 700 }}>
            [Gráfica aquí]
          </Box>
        </Paper>
        {/* Table Section */}
        <Paper elevation={6} sx={{ p: 4, borderRadius: 4, boxShadow: '0 8px 32px #3949ab22' }}>
          <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 2 }}>
            <Typography variant="h6" fontWeight={700} color="#23272f">Últimas Ventas</Typography>
            <Button size="small" variant="outlined" sx={{ borderColor: '#00bcd4', color: '#00bcd4', fontWeight: 700, ':hover': { bgcolor: '#e3f0ff', borderColor: '#3949ab', color: '#3949ab' } }}>
              Ver todas
            </Button>
          </Box>
          <Divider sx={{ mb: 2 }} />
          <Box sx={{ overflowX: 'auto' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr style={{ background: '#f5f7fa' }}>
                  <th style={{ padding: 12, textAlign: 'left', color: '#3949ab', fontWeight: 700 }}>Cliente</th>
                  <th style={{ padding: 12, textAlign: 'left', color: '#3949ab', fontWeight: 700 }}>Producto</th>
                  <th style={{ padding: 12, textAlign: 'left', color: '#3949ab', fontWeight: 700 }}>Total</th>
                  <th style={{ padding: 12, textAlign: 'left', color: '#3949ab', fontWeight: 700 }}>Fecha</th>
                </tr>
              </thead>
              <tbody>
                {tableData.map((row) => (
                  <tr key={row.id} style={{ borderBottom: '1px solid #e0e0e0' }}>
                    <td style={{ padding: 12 }}>{row.cliente}</td>
                    <td style={{ padding: 12 }}>{row.producto}</td>
                    <td style={{ padding: 12 }}>{row.total}</td>
                    <td style={{ padding: 12 }}>{row.fecha}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </Box>
        </Paper>
      </Box>
    {/* Botón flotante de chat */}
    <Button
      variant="contained"
      color="primary"
      onClick={() => setOpenChat(true)}
      sx={{
        position: 'fixed',
        bottom: 32,
        right: 32,
        zIndex: 2000,
        minWidth: 0,
        width: 64,
        height: 64,
        borderRadius: '50%',
        boxShadow: '0 4px 24px #3949ab55',
        bgcolor: '#00bcd4',
        ':hover': { bgcolor: '#0097a7' },
        display: { xs: 'none', sm: 'inline-flex' }
      }}
    >
      <ChatIcon sx={{ fontSize: 36, color: '#fff' }} />
    </Button>

    {/* Modal de chat */}
    <Dialog open={openChat} onClose={() => setOpenChat(false)} maxWidth="xs" fullWidth>
      <Box sx={{ p: 0, bgcolor: '#f8fafc', minHeight: 420, display: 'flex', flexDirection: 'column', height: 480 }}>
        <Box sx={{ bgcolor: '#00bcd4', color: '#fff', p: 2, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <Typography variant="h6" fontWeight={700}>Chat Soporte</Typography>
          <Button onClick={() => setOpenChat(false)} sx={{ color: '#fff', minWidth: 0 }}>X</Button>
        </Box>
        <Box sx={{ flex: 1, p: 2, overflowY: 'auto', display: 'flex', flexDirection: 'column', gap: 1 }}>
          {chatMessages.map((msg, i) => (
            <Box key={i} sx={{ alignSelf: msg.from === 'user' ? 'flex-end' : 'flex-start', bgcolor: msg.from === 'user' ? '#3949ab' : '#e3f2fd', color: msg.from === 'user' ? '#fff' : '#23272f', px: 2, py: 1, borderRadius: 3, mb: 0.5, maxWidth: '80%' }}>
              {msg.text}
            </Box>
          ))}
        </Box>
        <Box component="form" onSubmit={handleSend} sx={{ display: 'flex', p: 2, gap: 1, borderTop: '1px solid #e0e0e0' }}>
          <input
            type="text"
            value={chatInput}
            onChange={e => setChatInput(e.target.value)}
            placeholder="Escribe tu mensaje..."
            style={{ flex: 1, border: 'none', outline: 'none', fontSize: 16, padding: 8, borderRadius: 4, background: '#fff' }}
          />
          <Button type="submit" variant="contained" sx={{ bgcolor: '#3949ab', minWidth: 0, px: 2, fontWeight: 700 }}>Enviar</Button>
        </Box>
      </Box>
    </Dialog>
    </Box>
  );
};

export default UserDashboard;
