import React, { useState } from 'react';
import Dialog from '@mui/material/Dialog';
import Login from './Login';
import Register from './Register';
import { Box, Typography, Paper, Grid, Button, Avatar, Stack, Divider } from '@mui/material';
import StorefrontIcon from '@mui/icons-material/Storefront';
import PeopleIcon from '@mui/icons-material/People';
import Inventory2Icon from '@mui/icons-material/Inventory2';

// Importa la imagen local
import logo from './logo.png';

const testimonios = [
  {
    nombre: 'María López',
    cargo: 'Gerente de Ventas',
    empresa: 'Comercializadora Nova',
    mensaje: 'Desde que usamos este sistema, nuestro equipo es más productivo y los clientes están más felices.',
    foto: 'https://randomuser.me/api/portraits/women/44.jpg'
  },
  {
    nombre: 'Carlos Pérez',
    cargo: 'CEO',
    empresa: 'Distribuciones Pérez',
    mensaje: 'La plataforma es intuitiva, rápida y el soporte es excelente. ¡Recomendado 100%!',
    foto: 'https://randomuser.me/api/portraits/men/32.jpg'
  },
  {
    nombre: 'Ana Torres',
    cargo: 'Administradora',
    empresa: 'Soluciones Integrales',
    mensaje: 'Me encanta la facilidad para generar reportes y el diseño tan profesional.',
    foto: 'https://randomuser.me/api/portraits/women/68.jpg'
  }
];

const features = [
  {
    icon: <StorefrontIcon sx={{ fontSize: 40, color: '#00bcd4' }} />,
    title: 'Ventas Inteligentes',
    desc: 'Gestiona tus ventas de forma ágil y visualiza el crecimiento de tu negocio en tiempo real.'
  },
  {
    icon: <PeopleIcon sx={{ fontSize: 40, color: '#3949ab' }} />,
    title: 'Clientes Felices',
    desc: 'Conoce a tus clientes, mejora la atención y fidelízalos con herramientas personalizadas.'
  },
  {
    icon: <Inventory2Icon sx={{ fontSize: 40, color: '#ff9800' }} />,
    title: 'Inventario al Día',
    desc: 'Controla tu stock y recibe alertas para que nunca falte lo que tus clientes buscan.'
  }
];

const pasos = [
  {
    titulo: '1. Regístrate gratis',
    desc: 'Crea tu cuenta en segundos y comienza a gestionar tu negocio sin complicaciones.',
    color: '#00bcd4'
  },
  {
    titulo: '2. Agrega tus productos y clientes',
    desc: 'Carga tu inventario y tu base de clientes de manera sencilla y rápida.',
    color: '#3949ab'
  },
  {
    titulo: '3. ¡Comienza a vender!',
    desc: 'Emite facturas, consulta reportes y haz crecer tu empresa con información en tiempo real.',
    color: '#ff9800'
  }
];

const partners = [
  { src: 'https://upload.wikimedia.org/wikipedia/commons/4/44/Microsoft_logo.svg', alt: 'Microsoft' },
  { src: 'https://upload.wikimedia.org/wikipedia/commons/a/a7/React-icon.svg', alt: 'React' },
  { src: 'https://upload.wikimedia.org/wikipedia/commons/6/64/Expressjs.png', alt: 'Express' },
  { src: 'https://upload.wikimedia.org/wikipedia/commons/9/93/MongoDB_Logo.svg', alt: 'MongoDB' }
];

const Dashboard = ({ clientesCount, productosCount, onLogin, onRegister }) => {
  const [openLogin, setOpenLogin] = useState(false);
  const [openRegister, setOpenRegister] = useState(false);

  React.useEffect(() => {
    window.openLoginModal = () => setOpenLogin(true);
    window.openRegisterModal = () => setOpenRegister(true);
  }, []);

  const handleLogin = (...args) => {
    setOpenLogin(false);
    onLogin && onLogin(...args);
  };

  const handleRegister = (...args) => {
    setOpenRegister(false);
    onRegister && onRegister(...args);
  };

  return (
    <>
      {/* Modales */}
      <Dialog open={openLogin} onClose={() => setOpenLogin(false)} maxWidth="xs" fullWidth>
        <Login onLogin={handleLogin} />
      </Dialog>
      <Dialog open={openRegister} onClose={() => setOpenRegister(false)} maxWidth="xs" fullWidth>
        <Register onRegister={handleRegister} />
      </Dialog>

      <Box sx={{
        mt: 0,
        minHeight: '100vh',
        minWidth: '100vw',
        width: '100vw',
        pb: 12,
        bgcolor: 'linear-gradient(135deg, #e3f0ff 0%, #f8fafc 100%)',
        position: 'relative',
        overflow: 'hidden',
        left: '50%',
        right: '50%',
        marginLeft: '-50vw',
        marginRight: '-50vw',
        boxSizing: 'border-box'
      }}>
        {/* Fondo decorativo */}
        <StorefrontIcon sx={{
          position: 'absolute',
          top: 80,
          left: -60,
          fontSize: 320,
          color: '#3949ab11',
          zIndex: 0,
          display: { xs: 'none', md: 'block' }
        }} />
        <Inventory2Icon sx={{
          position: 'absolute',
          bottom: 120,
          right: -80,
          fontSize: 300,
          color: '#00bcd411',
          zIndex: 0,
          display: { xs: 'none', md: 'block' }
        }} />

        {/* Hero Section */}
        <Box sx={{
          background: 'linear-gradient(120deg, #23272f 60%, #3949ab 100%)',
          color: 'white',
          py: { xs: 5, md: 7 },
          px: { xs: 1, sm: 2, md: 6 },
          textAlign: 'center',
          borderRadius: 0,
          mb: { xs: 3, md: 4 },
          boxShadow: 8,
          position: 'relative',
          overflow: 'hidden',
          width: '100vw',
          maxWidth: '100vw',
          left: 0,
          mx: 0,
          zIndex: 1
        }}>
          <Box sx={{ display: 'flex', flexDirection: { xs: 'column', md: 'row' }, alignItems: 'center', justifyContent: 'center', mb: 4, gap: 4 }}>
            <Box sx={{ flex: 1, display: 'flex', justifyContent: 'center' }}>
              <img src={logo} alt="Ilustración ventas" style={{ width: 220, borderRadius: 24, boxShadow: '0 4px 24px #3949ab33' }} />
            </Box>
            <Box sx={{ flex: 2, textAlign: { xs: 'center', md: 'left' } }}>
              <Typography variant="h2" fontWeight={900} mb={1} sx={{ letterSpacing: 1.5, textShadow: '0 2px 12px #23272f99', color: '#fff', fontSize: { xs: 32, sm: 40, md: 48 }, fontFamily: 'Montserrat, Roboto, Arial', lineHeight: 1.1 }}>
                ¡Impulsa tu negocio con nosotros!
              </Typography>
              <Typography variant="h5" mb={2} sx={{ fontWeight: 600, opacity: 0.97, color: '#e3f0ff', fontSize: { xs: 18, sm: 22, md: 26 }, fontFamily: 'Montserrat, Roboto, Arial', lineHeight: 1.2 }}>
                Más que un sistema, somos tu aliado estratégico.
              </Typography>
              <Divider sx={{ bgcolor: '#fff', opacity: 0.2, my: 2, mx: { xs: 'auto', md: 0 }, width: 200 }} />
              <Typography variant="body1" mb={3} sx={{ maxWidth: 600, fontSize: { xs: 15, sm: 17, md: 19 }, opacity: 0.97, color: '#e3f0ff', fontFamily: 'Roboto, Arial', lineHeight: 1.5 }}>
                Nuestra misión es ayudarte a crecer. Con herramientas modernas, soporte humano y una experiencia pensada para personas reales, tu empresa tendrá el control y la confianza que necesita para destacar.
              </Typography>
              <Box sx={{ display: 'flex', gap: 2, flexDirection: { xs: 'column', sm: 'row' }, justifyContent: { xs: 'center', md: 'flex-start' } }}>
                <Button variant="contained" size="large" sx={{ bgcolor: '#00bcd4', color: '#fff', fontWeight: 700, px: 4, boxShadow: 2, ':hover': { bgcolor: '#0097a7' } }} onClick={() => setOpenRegister(true)}>
                  ¡Quiero ser parte!
                </Button>
                <Button variant="outlined" size="large" sx={{ color: '#fff', borderColor: '#fff', fontWeight: 700, px: 4, boxShadow: 2, ':hover': { bgcolor: '#3949ab22', borderColor: '#00bcd4', color: '#00bcd4' } }} onClick={() => {
                  const el = document.getElementById('features-section');
                  if (el) el.scrollIntoView({ behavior: 'smooth', block: 'start' });
                }}>
                  Ver beneficios
                </Button>
              </Box>
            </Box>
          </Box>
        </Box>

        {/* Features Section */}
        <Box id="features-section" sx={{ maxWidth: 1100, mx: 'auto', mb: 7 }}>
          <Grid container spacing={4}>
            {features.map((f, i) => (
              <Grid item xs={12} md={4} key={i}>
                <Paper elevation={6} sx={{ p: 4, borderRadius: 4, textAlign: 'center', transition: '0.3s', ':hover': { transform: 'translateY(-8px)', boxShadow: '0 12px 36px #3949ab22' } }}>
                  <Box mb={2}>{f.icon}</Box>
                  <Typography variant="h6" fontWeight={700} mb={1}>{f.title}</Typography>
                  <Typography variant="body1" color="text.secondary">{f.desc}</Typography>
                </Paper>
              </Grid>
            ))}
          </Grid>
        </Box>

        {/* Pasos Section */}
        <Box sx={{ maxWidth: 1200, mx: 'auto', mb: 7 }}>
          <Typography variant="h4" fontWeight={900} mb={4} textAlign="center">Cómo empezar</Typography>
          <Grid container spacing={4} justifyContent="center" alignItems="stretch" wrap="nowrap" sx={{
            flexDirection: { xs: 'column', md: 'row' },
            overflowX: { xs: 'auto', md: 'visible' }
          }}>
            {pasos.map((p, i) => (
              <Grid item xs={12} md={4} key={i} sx={{ minWidth: { xs: 300, md: 'unset' }, display: 'flex', justifyContent: 'center' }}>
                <Paper elevation={6} sx={{
                  p: 3,
                  borderRadius: 8,
                  bgcolor: '#fff',
                  minHeight: 160,
                  borderLeft: `8px solid ${p.color}`,
                  width: { xs: 280, md: '100%' },
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'flex-start',
                  justifyContent: 'center',
                  transition: 'transform 0.3s cubic-bezier(.4,2,.6,1), box-shadow 0.3s',
                  cursor: 'pointer',
                  '&:hover': {
                    transform: 'translateY(-8px) scale(1.03)',
                    boxShadow: '0 12px 36px #3949ab22',
                    bgcolor: '#f5f7fa',
                  }
                }}>
                  <Typography variant="h6" fontWeight={700} color={p.color} mb={1}>{p.titulo}</Typography>
                  <Typography variant="body1" color="text.secondary">{p.desc}</Typography>
                </Paper>
              </Grid>
            ))}
          </Grid>
        </Box>

        {/* Testimonios Section */}
        <Box sx={{ maxWidth: 1100, mx: 'auto', mb: 7 }}>
          <Typography variant="h4" fontWeight={900} mb={4} textAlign="center">Testimonios</Typography>
          <Grid container spacing={4}>
            {testimonios.map((t, i) => (
              <Grid item xs={12} md={4} key={i}>
                <Paper elevation={3} sx={{ p: 3, borderRadius: 4, textAlign: 'center' }}>
                  <Avatar src={t.foto} alt={t.nombre} sx={{ width: 64, height: 64, mx: 'auto', mb: 2 }} />
                  <Typography variant="h6" fontWeight={700}>{t.nombre}</Typography>
                  <Typography variant="body2" color="text.secondary">{t.cargo} - {t.empresa}</Typography>
                  <Divider sx={{ my: 1 }} />
                  <Typography variant="body2" color="text.secondary">{t.mensaje}</Typography>
                </Paper>
              </Grid>
            ))}
          </Grid>
        </Box>

        {/* Partners Section */}
        <Box sx={{ maxWidth: 900, mx: 'auto', mt: 4, display: 'flex', justifyContent: 'space-around', flexWrap: 'wrap', gap: 4 }}>
          {partners.map((p, i) => (
            <img key={i} src={p.src} alt={p.alt} style={{ height: 48, objectFit: 'contain', opacity: 0.8 }} />
          ))}
        </Box>

      </Box>
    </>
  );
};

export default Dashboard;
