import React, { useState, useMemo } from 'react';
import { BarChart3, Users, Package, TrendingUp, LogOut, Plus, BarChart, User, ShoppingCart } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, BarChart as RechartsBarChart, Bar } from 'recharts';

const initialVentas = [
  { id: 1, cliente: 'María López', producto: 'Laptop', total: 1200, fecha: '22/08/2025' },
  { id: 2, cliente: 'Carlos Pérez', producto: 'Mouse', total: 25, fecha: '22/08/2025' },
  { id: 3, cliente: 'Ana Torres', producto: 'Monitor', total: 180, fecha: '21/08/2025' },
  { id: 4, cliente: 'Luis Gómez', producto: 'Teclado', total: 45, fecha: '21/08/2025' },
  { id: 5, cliente: 'María López', producto: 'Mouse', total: 25, fecha: '20/08/2025' },
  { id: 6, cliente: 'Carlos Pérez', producto: 'Teclado', total: 45, fecha: '20/08/2025' },
  { id: 7, cliente: 'Sofía Ramírez', producto: 'Monitor', total: 180, fecha: '19/08/2025' },
  { id: 8, cliente: 'Diego Morales', producto: 'Laptop', total: 1200, fecha: '19/08/2025' },
  { id: 9, cliente: 'Elena Vargas', producto: 'Mouse', total: 25, fecha: '18/08/2025' },
  { id: 10, cliente: 'Roberto Silva', producto: 'Monitor', total: 180, fecha: '18/08/2025' },
];

const productos = [
  { label: 'Laptop', value: 'Laptop' },
  { label: 'Mouse', value: 'Mouse' },
  { label: 'Monitor', value: 'Monitor' },
  { label: 'Teclado', value: 'Teclado' },
];

const UserDashboard = () => {
  const [activeView, setActiveView] = useState('dashboard');
  const [ventas, setVentas] = useState(initialVentas);
  const [openVenta, setOpenVenta] = useState(false);
  const [ventaForm, setVentaForm] = useState({ cliente: '', producto: '', total: '', fecha: '' });
  const [ventaError, setVentaError] = useState('');

  // Calcular métricas dinámicamente
  const metrics = useMemo(() => {
    const ventasHoy = ventas.filter(v => v.fecha === new Date().toLocaleDateString('es-MX'));
    const totalHoy = ventasHoy.reduce((sum, v) => sum + v.total, 0);
    const clientesUnicos = [...new Set(ventas.map(v => v.cliente))].length;
    const productosUnicos = [...new Set(ventas.map(v => v.producto))].length;

    return [
      { 
        label: 'Ventas Hoy', 
        value: `$${totalHoy.toLocaleString()}`, 
        icon: <BarChart3 className="text-cyan-500" size={36} />, 
        bgColor: 'bg-blue-50' 
      },
      { 
        label: 'Clientes', 
        value: clientesUnicos.toString(), 
        icon: <Users className="text-indigo-600" size={36} />, 
        bgColor: 'bg-yellow-50' 
      },
      { 
        label: 'Productos', 
        value: productosUnicos.toString(), 
        icon: <Package className="text-orange-500" size={36} />, 
        bgColor: 'bg-orange-50' 
      },
    ];
  }, [ventas]);

  // Datos para gráficas
  const datosGraficas = useMemo(() => {
    // Ventas por día
    const ventasPorDia = ventas.reduce((acc, venta) => {
      const fecha = venta.fecha;
      if (!acc[fecha]) {
        acc[fecha] = 0;
      }
      acc[fecha] += venta.total;
      return acc;
    }, {});

    const ventasDiarias = Object.entries(ventasPorDia)
      .map(([fecha, total]) => ({ fecha, total }))
      .sort((a, b) => new Date(a.fecha.split('/').reverse().join('-')) - new Date(b.fecha.split('/').reverse().join('-')));

    // Productos más vendidos
    const productosMasVendidos = ventas.reduce((acc, venta) => {
      if (!acc[venta.producto]) {
        acc[venta.producto] = { producto: venta.producto, cantidad: 0, total: 0 };
      }
      acc[venta.producto].cantidad += 1;
      acc[venta.producto].total += venta.total;
      return acc;
    }, {});

    const topProductos = Object.values(productosMasVendidos)
      .sort((a, b) => b.total - a.total);

    return { ventasDiarias, topProductos };
  }, [ventas]);

  // Datos de clientes
  const clientesData = useMemo(() => {
    const clientesInfo = ventas.reduce((acc, venta) => {
      if (!acc[venta.cliente]) {
        acc[venta.cliente] = {
          nombre: venta.cliente,
          totalCompras: 0,
          numeroCompras: 0,
          ultimaCompra: venta.fecha,
          productos: []
        };
      }
      acc[venta.cliente].totalCompras += venta.total;
      acc[venta.cliente].numeroCompras += 1;
      acc[venta.cliente].productos.push(venta.producto);
      
      // Actualizar última compra si es más reciente
      const fechaActual = new Date(venta.fecha.split('/').reverse().join('-'));
      const fechaUltima = new Date(acc[venta.cliente].ultimaCompra.split('/').reverse().join('-'));
      if (fechaActual > fechaUltima) {
        acc[venta.cliente].ultimaCompra = venta.fecha;
      }
      
      return acc;
    }, {});

    return Object.values(clientesInfo).sort((a, b) => b.totalCompras - a.totalCompras);
  }, [ventas]);

  const handleOpenVenta = () => {
    setVentaForm({ cliente: '', producto: '', total: '', fecha: new Date().toLocaleDateString('es-MX') });
    setVentaError('');
    setOpenVenta(true);
  };

  const handleChangeVenta = (e) => {
    setVentaForm({ ...ventaForm, [e.target.name]: e.target.value });
  };

  const handleGuardarVenta = () => {
    if (!ventaForm.cliente || !ventaForm.producto || !ventaForm.total || isNaN(Number(ventaForm.total))) {
      setVentaError('Todos los campos son obligatorios y el total debe ser un número');
      return;
    }
    setVentas([
      { ...ventaForm, id: ventas.length + 1, total: Number(ventaForm.total), fecha: ventaForm.fecha || new Date().toLocaleDateString('es-MX') },
      ...ventas
    ]);
    setOpenVenta(false);
  };

  const NavItem = ({ icon, label, isActive, onClick }) => (
    <button
      onClick={onClick}
      className={`w-full flex items-center px-4 py-3 text-left rounded-lg transition-colors ${
        isActive 
          ? 'bg-white bg-opacity-20 text-white' 
          : 'text-gray-300 hover:bg-white hover:bg-opacity-10 hover:text-white'
      }`}
    >
      {icon}
      <span className="ml-3 hidden md:block">{label}</span>
    </button>
  );

  return (
    <div className="flex min-h-screen bg-gradient-to-br from-blue-50 to-slate-50">
      {/* Sidebar */}
      <div className="w-16 md:w-64 bg-gradient-to-b from-slate-800 to-indigo-600 text-white shadow-2xl">
        <div className="p-4">
          <div className="flex items-center mb-8">
            <div className="w-12 h-12 bg-cyan-500 rounded-full flex items-center justify-center mr-3">
              <span className="text-white font-bold text-xl">U</span>
            </div>
            <div className="hidden md:block">
              <h2 className="text-white font-bold text-xl">Usuario</h2>
            </div>
          </div>
          
          <nav className="space-y-2">
            <NavItem 
              icon={<BarChart3 size={24} />} 
              label="Dashboard" 
              isActive={activeView === 'dashboard'}
              onClick={() => setActiveView('dashboard')}
            />
            <NavItem 
              icon={<Users size={24} />} 
              label="Clientes" 
              isActive={activeView === 'clientes'}
              onClick={() => setActiveView('clientes')}
            />
            <NavItem 
              icon={<Package size={24} />} 
              label="Productos" 
              isActive={activeView === 'productos'}
              onClick={() => setActiveView('productos')}
            />
            <NavItem 
              icon={<BarChart size={24} />} 
              label="Ventas" 
              isActive={activeView === 'ventas'}
              onClick={() => setActiveView('ventas')}
            />
          </nav>
        </div>
        
        <div className="absolute bottom-4 left-4 right-4">
          <button className="w-full flex items-center px-4 py-3 text-gray-300 hover:text-white transition-colors">
            <LogOut size={24} />
            <span className="ml-3 hidden md:block">Cerrar sesión</span>
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-4 md:p-8">
        {activeView === 'dashboard' && (
          <div className="space-y-6">
            {/* Header */}
            <div className="flex justify-between items-center">
              <h1 className="text-4xl font-black text-slate-800">Panel de Control</h1>
              <button
                onClick={handleOpenVenta}
                className="bg-cyan-500 hover:bg-cyan-600 text-white px-6 py-3 rounded-lg font-bold flex items-center shadow-lg transition-colors"
              >
                <Plus size={20} className="mr-2" />
                Nueva Venta
              </button>
            </div>

            {/* Metrics Cards */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {metrics.map((metric, i) => (
                <div key={i} className={`${metric.bgColor} p-6 rounded-2xl shadow-lg border border-gray-100`}>
                  <div className="flex items-center">
                    {metric.icon}
                    <div className="ml-4">
                      <p className="text-gray-600 text-sm">{metric.label}</p>
                      <p className="text-2xl font-bold text-slate-800">{metric.value}</p>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {/* Charts */}
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
              <div className="lg:col-span-2 bg-white p-6 rounded-2xl shadow-lg">
                <h3 className="text-xl font-bold text-indigo-600 mb-4">Ventas Diarias</h3>
                <ResponsiveContainer width="100%" height={300}>
                  <LineChart data={datosGraficas.ventasDiarias}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="fecha" />
                    <YAxis />
                    <Tooltip formatter={(value) => [`${value.toLocaleString()}`, 'Total']} />
                    <Legend />
                    <Line type="monotone" dataKey="total" stroke="#06b6d4" strokeWidth={3} dot={{ fill: '#06b6d4', strokeWidth: 2, r: 6 }} />
                  </LineChart>
                </ResponsiveContainer>
              </div>
              
              <div className="bg-white p-6 rounded-2xl shadow-lg">
                <h3 className="text-xl font-bold text-indigo-600 mb-4">Productos Más Vendidos</h3>
                <ResponsiveContainer width="100%" height={300}>
                  <RechartsBarChart data={datosGraficas.topProductos} layout="horizontal">
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis type="number" />
                    <YAxis dataKey="producto" type="category" width={80} />
                    <Tooltip formatter={(value) => [`${value.toLocaleString()}`, 'Total']} />
                    <Bar dataKey="total" fill="#3949ab" />
                  </RechartsBarChart>
                </ResponsiveContainer>
              </div>
            </div>
          </div>
        )}

        {activeView === 'clientes' && (
          <div className="space-y-6">
            <div className="flex justify-between items-center">
              <h1 className="text-4xl font-black text-slate-800">Clientes</h1>
              <p className="text-gray-600">Total: {clientesData.length} clientes</p>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {clientesData.map((cliente, index) => (
                <div key={index} className="bg-white p-6 rounded-2xl shadow-lg border border-gray-100">
                  <div className="flex items-center mb-4">
                    <div className="w-12 h-12 bg-indigo-600 rounded-full flex items-center justify-center mr-3">
                      <User className="text-white" size={20} />
                    </div>
                    <div>
                      <h3 className="text-lg font-bold text-slate-800">{cliente.nombre}</h3>
                      <p className="text-gray-600 text-sm">Cliente #{index + 1}</p>
                    </div>
                  </div>
                  
                  <div className="grid grid-cols-2 gap-4 mb-4">
                    <div className="text-center">
                      <p className="text-2xl font-bold text-cyan-500">${cliente.totalCompras.toLocaleString()}</p>
                      <p className="text-gray-600 text-sm">Total Compras</p>
                    </div>
                    <div className="text-center">
                      <p className="text-2xl font-bold text-orange-500">{cliente.numeroCompras}</p>
                      <p className="text-gray-600 text-sm">Num. Compras</p>
                    </div>
                  </div>

                  <div className="border-t pt-4">
                    <p className="text-gray-600 text-sm mb-1">Última compra:</p>
                    <p className="font-semibold mb-3">{cliente.ultimaCompra}</p>
                    
                    <p className="text-gray-600 text-sm mb-2">Productos comprados:</p>
                    <div className="flex flex-wrap gap-1">
                      {[...new Set(cliente.productos)].map((producto, i) => (
                        <span key={i} className="bg-blue-100 text-indigo-600 px-2 py-1 rounded-lg text-xs font-semibold">
                          {producto}
                        </span>
                      ))}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeView === 'productos' && (
          <div className="space-y-6">
            <h1 className="text-4xl font-black text-slate-800">Productos</h1>
            <div className="bg-white p-6 rounded-2xl shadow-lg">
              <h3 className="text-xl font-bold text-indigo-600 mb-4">Lista de Productos Disponibles</h3>
              <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4">
                {productos.map((producto, index) => (
                  <div key={index} className="bg-gray-50 p-6 rounded-xl text-center border-2 border-blue-100">
                    <Package className="mx-auto mb-3 text-indigo-600" size={48} />
                    <h4 className="text-lg font-bold text-slate-800">{producto.label}</h4>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {activeView === 'ventas' && (
          <div className="space-y-6">
            <div className="flex justify-between items-center">
              <h1 className="text-4xl font-black text-slate-800">Ventas</h1>
              <button
                onClick={handleOpenVenta}
                className="bg-cyan-500 hover:bg-cyan-600 text-white px-6 py-3 rounded-lg font-bold flex items-center shadow-lg transition-colors"
              >
                <Plus size={20} className="mr-2" />
                Nueva Venta
              </button>
            </div>
            
            <div className="bg-white rounded-2xl shadow-lg overflow-hidden">
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-4 text-left text-indigo-600 font-bold">Cliente</th>
                      <th className="px-6 py-4 text-left text-indigo-600 font-bold">Producto</th>
                      <th className="px-6 py-4 text-left text-indigo-600 font-bold">Total</th>
                      <th className="px-6 py-4 text-left text-indigo-600 font-bold">Fecha</th>
                    </tr>
                  </thead>
                  <tbody>
                    {ventas.map((venta) => (
                      <tr key={venta.id} className="border-b border-gray-100">
                        <td className="px-6 py-4">{venta.cliente}</td>
                        <td className="px-6 py-4">{venta.producto}</td>
                        <td className="px-6 py-4">${venta.total.toLocaleString()}</td>
                        <td className="px-6 py-4">{venta.fecha}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        )}

        {/* Modal Nueva Venta */}
        {openVenta && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded-2xl shadow-2xl w-full max-w-md mx-4">
              <h2 className="text-2xl font-bold text-slate-800 mb-4">Nueva Venta</h2>
              
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">Cliente</label>
                  <input
                    type="text"
                    name="cliente"
                    value={ventaForm.cliente}
                    onChange={handleChangeVenta}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                    placeholder="Nombre del cliente"
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">Producto</label>
                  <select
                    name="producto"
                    value={ventaForm.producto}
                    onChange={handleChangeVenta}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                  >
                    <option value="">Seleccionar producto</option>
                    {productos.map((p) => (
                      <option key={p.value} value={p.value}>{p.label}</option>
                    ))}
                  </select>
                </div>
                
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">Total ($)</label>
                  <input
                    type="number"
                    name="total"
                    value={ventaForm.total}
                    onChange={handleChangeVenta}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                    placeholder="0"
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">Fecha</label>
                  <input
                    type="text"
                    name="fecha"
                    value={ventaForm.fecha}
                    onChange={handleChangeVenta}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                  />
                </div>
                
                {ventaError && (
                  <p className="text-red-500 text-sm">{ventaError}</p>
                )}
              </div>
              
              <div className="flex space-x-4 mt-6">
                <button
                  onClick={() => setOpenVenta(false)}
                  className="flex-1 px-4 py-3 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50 transition-colors"
                >
                  Cancelar
                </button>
                <button
                  onClick={handleGuardarVenta}
                  className="flex-1 px-4 py-3 bg-cyan-500 text-white rounded-lg hover:bg-cyan-600 transition-colors"
                >
                  Guardar
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};
